{
  "name": "network-topology-copy",
  "label": "Network Topology",
  "description": "Network Topology",
  "dashboard_style": "auto",
  "version": "23.7.8.1",
  "enabled": true,
  "dashboard_type": "dashboard",
  "custom_context": {
    "alert-context": true,
    "node_view_params": {
      "stream": "oia-alerts-stream",
      "extra_filter": "a_asset_ip_address is '${node_id}' and a_status = 'ACTIVE' and a_en_link_type is not empty and a_source_systemname is not 'Alert Group' and a_correlation_status is not 'AGGREGATE'",
      "title": "Active Alerts ${node_id}",
      "duration_hours": 1,
      "columns": {
        "a_asset_ip_address": { "title": "Device" },
        "a_en_local_interface": { "title": "Interface" },
        "a_en_remote_ip": { "title": "Remote Device" },
        "a_en_remote_interface": { "title": "Remote Interface" },
        "a_en_alert_name": { "title": "Alert Name" },
        "a_en_link_type": { "title": "Link Type" },
        "a_severity": { "title": "Alert severity" },
        "a_incident_id": { "title": "Incident ID" },
        "a_message": { "title": "Message" },
        "timestamp": { "title": "timestamp" }
      },
      "severity_columns": ["a_severity"]
    },
    "link_view_params": {
      "stream": "oia-alerts-stream",
      "extra_filter": "(a_asset_ip_address is '${source}' and a_en_remote_ip is '${target}') or (a_asset_ip_address is '${target}' and a_en_remote_ip is '${source}') and a_status = 'ACTIVE' and a_en_link_type is not empty and a_source_systemname is not 'Alert Group' and a_correlation_status is not 'AGGREGATE'",
      "title": "Active Alerts on ${source} <-> ${target}",
      "duration_hours": 1,
      "columns": {
        "a_asset_ip_address": { "title": "Device" },
        "a_en_local_interface": { "title": "Interface" },
        "a_en_remote_ip": { "title": "Remote Device" },
        "a_en_remote_interface": { "title": "Remote Interface" },
        "a_en_alert_name": { "title": "Alert Name" },
        "a_en_link_type": { "title": "Link Type" },
        "a_severity": { "title": "Alert severity" },
        "a_incident_id": { "title": "Incident ID" },
        "a_message": { "title": "Message" },
        "timestamp": { "title": "timestamp" }
      },
      "severity_columns": ["a_severity"]
    }
  },
  "dashboard_filters": {
    "time_filter": true,
    "columns_filter": []
  },
  "dashboard_sections": [
    {
      "title": "Topology",
      "show_filter": true,
      "widgets": [
        {
          "widget_type": "topology",
          "title": "Topology",
          "actions": [
            {
              "permission": "rda:dataset:add",
              "actionCondition": {
                "actionControl": "SHOW_IF",
                "conditionalField": [
                  {
                    "conditionType": "EQUAL",
                    "conditionValue": "Network",
                    "fieldId": "layer"
                  }
                ]
              },
              "api-endpoint": {
                "service-name": "saas-reports",
                "methodName": "getReport",
                "stringified-params": false,
                "parse-output": false,
                "params": [
                  {
                    "reportId": "rda.saas.topology.stream.nodes.alerts"
                  }
                ]
              },
              "icon": "mdi mdi-plus",
              "identifier": "rda.saas.topology.stream.nodes.alerts.view",
              "selectionType": "SINGLE",
              "title": "Show Node Alerts",
              "type": "POPUP_REPORT"
            },
            {
              "permission": "rda:dataset:add",
              "actionCondition": {
                "actionControl": "SHOW_IF",
                "conditionalField": [
                  {
                    "conditionType": "EQUAL",
                    "conditionValue": "connects-to",
                    "fieldId": "edgeType"
                  }
                ]
              },
              "api-endpoint": {
                "service-name": "saas-reports",
                "methodName": "getReport",
                "stringified-params": false,
                "parse-output": false,
                "params": [
                  {
                    "reportId": "rda.saas.topology.stream.links.alerts",
                    "hello": "world SIRI"
                  }
                ]
              },
              "icon": "mdi mdi-plus",
              "identifier": "rda.saas.topology.stream.links.alerts.view",
              "selectionType": "SINGLE",
              "title": "Show Link Alerts",
              "type": "POPUP_REPORT"
            }
          ],
          "chartProperties": {
            "hasToolbar": true,
            "shortToolbar": true,
            "userZoomingEnabled": false
          },
          "stack": "topology_stack",
          "max_width": 12,
          "min_width": 12,
          "height": 12,
          "alert_source": {
            "stream": "oia-alerts-stream",
            "group_by": ["a_asset_ip_address", "a_severity", "a_status"],
            "ts_column": "timestamp",
            "duration_hours": 1,
            "agg": "value_count",
            "column": "_id",
            "type": "int",
            "extra_filter": "a_en_link_type is not empty and a_source_systemname is not 'Alert Group' and a_correlation_status is not 'AGGREGATE' and a_status = 'ACTIVE'"
          },
          "link_alert_source": {
            "stream": "oia-alerts-stream",
            "group_by": ["left_id", "right_id"],
            "agg": "min",
            "column": "alert_severity_num",
            "ts_column": "timestamp",
            "duration_hours": 1440,
            "extra_filter": "a_en_link_type is not empty and a_source_systemname is not 'Alert Group' and a_correlation_status is not 'AGGREGATE' and a_status = 'ACTIVE'"
          },
          "widget_id": "9db5a36f"
        }
      ]
    }
  ]
}

